def getTeamsNew(depts, dept, depth = 0, ids = [])

  i = 0
  str = ''

  until i > depth
    str = str + '-'
    i = i + 1
  end

  if !dept[:child_ids].empty?
    dept[:child_ids].each do |childId|
      depts.each do |deptRow|
        if deptRow[:id] == childId
          if !ids.include?(deptRow[:id])

           if deptRow[:child_ids].empty? && deptRow[:jobs].empty?
            break
           else
             puts "#{str} #{deptRow[:name]}"
           end

          else
            break;
          end

          if deptRow[:child_ids].empty? && !deptRow[:jobs].empty?
            deptRow[:jobs].each do |job|
              puts "#{str}> #{job[:title]}"
            end
            break;
          else
            getTeamsNew(depts, deptRow, depth += 1, ids << deptRow[:id])
          end
        end
      end
    end
  end
end


def getTeams(dept, teams, parentId = 0, depth = 1, ids = [])
  i = 1
  str = ''
  if !dept[:child_ids].empty? && !ids.include?(dept[:id])
        dept[:child_ids].each do |child|
        teams.each do |team|
          if team[:id] == child
            until i > depth
              str = str + '-'
              i = i + 1
            end

    	    if !team[:child_ids].empty? || !team[:jobs].empty?
	    	    puts "#{str} #{team[:name]}"
		      end

      	  if team[:child_ids].empty? && !team[:jobs].empty?
	  	      getJobs(team, depth)
          end

		      if !team[:child_ids].empty? && team[:jobs].empty?
           getTeams(team, teams, team[:id], depth += 1, ids << team[:id])
          end
	      end
	    end
	  end
	end
end


def getJobs(team, depth = 2)
  i = 0
  str = ''
  if !team[:jobs].empty?
	  until i > depth
		  str = str + '-'
		  i = i + 1
		end
    team[:jobs].each do |job|
      puts "#{str}> #{job[:title]}"
    end
  end
end









